mapper.select:
SELECT member_id AS "memberId", password, name, email, phone, auth, TO_CHAR(join_date, 'yyyy-MM-dd') AS "joinDate", birth_date 
FROM member 
WHERE member_id = #{memberId}


SELECT 
  member_id,password,name,email,phone,auth,TO_CHAR(join_date, 'yyyy-MM-dd'),birth_date,is_membership 
FROM member 
WHERE member_id = #{memberId} 
  AND password = #{password}

mapper.insert:
INSERT INTO member (
  member_id, password, name, email, phone,  birth_date
) VALUES (
  #{memberId}, #{password}, #{name}, #{email}, #{phone}, #{birthDate}
)
