mapper.insert:
INSERT INTO member (
  member_id, password, name, email, phone, auth, join_date, birth_date
) VALUES (
  #{memberId}, #{password}, #{name}, #{email}, #{phone}, 'USER', SYSDATE, #{birthDate}
)

mapper.update:
update member 
set is_membership = 'O' 
where member_id = #{memberId}

mapper.select:
SELECT member_id, is_membership 
FROM member 
WHERE member_id = #{memberId}

mapper.update:
update member 
set is_membership = 'X' 
where member_id = #{memberId}
